import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://148.230.76.31:3001/api';
const BASE_URL = process.env.REACT_APP_BASE_URL || 'http://148.230.76.31:3001';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user') || 'null'));
  const [tela, setTela] = useState('venda');

  useEffect(() => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    }
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setToken(null);
    setUser(null);
  };

  const isAdmin = user?.permissao === 'admin';

  if (!token) {
    return <TelaLogin setToken={setToken} setUser={setUser} />;
  }

  return (
    <div className="App">
      <header className="header">
        <h1>ðŸŽ« PDV Visite Campos</h1>
        <div className="user-info">
          <span>ðŸ‘¤ {user?.nome}</span>
          <span className={`badge ${isAdmin ? 'badge-admin' : 'badge-user'}`}>
