// ============= TELA DE LOGIN =============
function TelaLogin({ setToken, setUser }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [erro, setErro] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setErro('');
    setLoading(true);

    try {
      const response = await axios.post(`${API_URL}/login`, { username, password });
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.operador));
      setToken(response.data.token);
      setUser(response.data.operador);
    } catch (error) {
      setErro(error.response?.data?.error || 'Erro ao fazer login');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>ðŸŽ« Visite Campos</h1>
        <h2>Sistema PDV</h2>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="UsuÃ¡rio"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Senha"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {erro && <div className="erro">{erro}</div>}
          <button type="submit" disabled={loading}>
            {loading ? 'Entrando...' : 'Entrar'}
          </button>
        </form>
      </div>
    </div>
  );
}

// ============= TELA DE VENDA =============
function TelaVenda() {
  const [nomeCliente, setNomeCliente] = useState('');
  const [produtoId, setProdutoId] = useState('');
  const [quantidadePessoas, setQuantidadePessoas] = useState(1);
  const [desconto, setDesconto] = useState('');
  const [tipoDesconto, setTipoDesconto] = useState('real');
  const [vendaOnline, setVendaOnline] = useState(false);
  const [telefoneCliente, setTelefoneCliente] = useState('');
  const [produtos, setProdutos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [mensagem, setMensagem] = useState('');
  const userLogado = JSON.parse(localStorage.getItem('user'));
  const isAdmin = userLogado?.permissao === 'admin';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      carregarProdutos();
    }
  }, []);

  const carregarProdutos = async () => {
    try {
      const response = await axios.get(`${API_URL}/produtos`);
      setProdutos(response.data);
    } catch (error) {
      console.error('Erro ao carregar produtos:', error);
      if (error.response && error.response.status !== 401) {
        alert('Erro ao carregar produtos');
      }
    }
  };

  const produtoSelecionado = produtos.find(p => p.id === parseInt(produtoId));
  const subtotal = produtoSelecionado ? produtoSelecionado.preco * quantidadePessoas : 0;

  let valorDesconto = 0;
  if (desconto && parseFloat(desconto) > 0) {
    if (tipoDesconto === 'percentual') {
      valorDesconto = (subtotal * parseFloat(desconto)) / 100;
    } else {
      valorDesconto = parseFloat(desconto);
    }
  }

  const total = subtotal - valorDesconto;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMensagem('');

    // Validar telefone obrigatÃ³rio
    if (!telefoneCliente) {
      alert('Por favor, informe o telefone do cliente');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(`${API_URL}/vendas`, {
        nome_cliente: nomeCliente,
        produto_id: parseInt(produtoId),
        quantidade_pessoas: parseInt(quantidadePessoas),
        desconto: valorDesconto,
        tipo_desconto: desconto ? tipoDesconto : null,
        venda_online: vendaOnline,
        telefone_cliente: telefoneCliente
      });

      // Baixar PDF
      const pdfResponse = await axios.get(`${API_URL}/vendas/${response.data.id}/pdf`, {
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([pdfResponse.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `ingresso_${response.data.codigo_venda}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();

      setMensagem(`âœ… Venda #${response.data.codigo_venda} realizada com sucesso!`);

      // Limpar formulÃ¡rio
      setNomeCliente('');
      setProdutoId('');
      setQuantidadePessoas(1);
      setDesconto('');
      setVendaOnline(false);
      setTelefoneCliente('');

      setTimeout(() => setMensagem(''), 5000);
    } catch (error) {
      alert(error.response?.data?.error || 'Erro ao registrar venda');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="tela-venda">
      <h2>ðŸ›’ Nova Venda</h2>

      {mensagem && <div className="mensagem-sucesso">{mensagem}</div>}

      <form onSubmit={handleSubmit} className="form-venda">
        {isAdmin && (
          <div className="form-group-checkbox">
            <label className="checkbox-label">
              <input
                type="checkbox"
                checked={vendaOnline}
                onChange={(e) => setVendaOnline(e.target.checked)}
              />
              <span className="checkbox-text">ðŸŒ Venda Online</span>
            </label>
          </div>
        )}

        <div className="form-group">
          <label>Nome do Cliente *</label>
          <input
            type="text"
            value={nomeCliente}
            onChange={(e) => setNomeCliente(e.target.value)}
            required
            placeholder="Digite o nome do cliente"
          />
        </div>

        <div className="form-group">
          <label>Telefone do Cliente (com DDD) *</label>
          <input
            type="tel"
            value={telefoneCliente}
            onChange={(e) => setTelefoneCliente(e.target.value)}
            required
            placeholder="Ex: 22999887766"
            pattern="[0-9]{10,11}"
          />
          <small style={{ color: '#666', fontSize: '0.85rem' }}>
            Digite apenas nÃºmeros (ex: 22999887766)
          </small>
        </div>

        <div className="form-group">
          <label>Produto *</label>
          <select
            value={produtoId}
            onChange={(e) => setProdutoId(e.target.value)}
            required
          >
            <option value="">Selecione um produto</option>
            {produtos.map(p => (
              <option key={p.id} value={p.id}>
                {p.nome} - R$ {p.preco.toFixed(2)} por pessoa
              </option>
            ))}
          </select>
        </div>

        {/* Preview do produto selecionado */}
        {produtoSelecionado && produtoSelecionado.foto && (
          <div className="produto-preview">
            <img 
              src={`${BASE_URL}${produtoSelecionado.foto}`} 
              alt={produtoSelecionado.nome}
              style={{ maxWidth: '100%', maxHeight: '200px', borderRadius: '8px', marginBottom: '1rem' }}
            />
          </div>
        )}

        <div className="form-group">
          <label>Quantidade de Pessoas *</label>
          <input
            type="number"
            min="1"
            value={quantidadePessoas}
            onChange={(e) => setQuantidadePessoas(e.target.value)}
            required
          />
        </div>

        {produtoSelecionado && (
          <div className="resumo-valores">
            <div className="linha-valor">
              <span>Subtotal:</span>
              <span className="valor">R$ {subtotal.toFixed(2)}</span>
            </div>
          </div>
        )}

        <div className="desconto-section">
          <h3>Desconto (opcional)</h3>
          <div className="desconto-inputs">
            <div className="form-group">
              <label>Tipo de Desconto</label>
              <select
                value={tipoDesconto}
                onChange={(e) => setTipoDesconto(e.target.value)}
              >
                <option value="real">R$ (Reais)</option>
                <option value="percentual">% (Porcentagem)</option>
              </select>
            </div>
            <div className="form-group">
              <label>Valor do Desconto</label>
              <input
                type="number"
                step="0.01"
                min="0"
                value={desconto}
                onChange={(e) => setDesconto(e.target.value)}
                placeholder={tipoDesconto === 'real' ? '0.00' : '0'}
              />
            </div>
          </div>

          {desconto && parseFloat(desconto) > 0 && (
            <div className="resumo-valores">
              <div className="linha-valor desconto">
                <span>Desconto:</span>
                <span className="valor">- R$ {valorDesconto.toFixed(2)}</span>
              </div>
            </div>
          )}
        </div>

        {produtoSelecionado && (
          <div className="total-final">
            <span>TOTAL:</span>
            <span className="valor-total">R$ {total.toFixed(2)}</span>
          </div>
        )}

        <button type="submit" disabled={loading} className="btn-finalizar">
          {loading ? 'Processando...' : vendaOnline ? 'ðŸŒ Finalizar Venda Online' : 'âœ… Finalizar Venda'}
        </button>
      </form>
    </div>
  );
}

// ============= TELA DE PRODUTOS =============
function TelaProdutos() {
  const [produtos, setProdutos] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editando, setEditando] = useState(null);
  const [nome, setNome] = useState('');
  const [preco, setPreco] = useState('');
  const [descricao, setDescricao] = useState('');
  const [foto, setFoto] = useState(null);
  const [previewFoto, setPreviewFoto] = useState('');

  useEffect(() => {
    carregarProdutos();
  }, []);

  const carregarProdutos = async () => {
    try {
      const response = await axios.get(`${API_URL}/produtos`);
      setProdutos(response.data);
    } catch (error) {
      alert('Erro ao carregar produtos');
    }
  };

  const handleFotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        alert('A imagem deve ter no mÃ¡ximo 5MB');
        return;
      }
      setFoto(file);
      setPreviewFoto(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('nome', nome);
    formData.append('preco', preco);
    formData.append('descricao', descricao);
    if (foto) {
      formData.append('foto', foto);
    }

    try {
      if (editando) {
        await axios.put(`${API_URL}/produtos/${editando}`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        alert('Produto atualizado com sucesso!');
      } else {
        await axios.post(`${API_URL}/produtos`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        alert('Produto cadastrado com sucesso!');
      }
      setNome('');
      setPreco('');
      setDescricao('');
      setFoto(null);
      setPreviewFoto('');
      setEditando(null);
      setShowForm(false);
      carregarProdutos();
    } catch (error) {
      alert(error.response?.data?.error || 'Erro ao salvar produto');
    }
  };

  const handleEditar = (produto) => {
    setEditando(produto.id);
    setNome(produto.nome);
    setPreco(produto.preco);
    setDescricao(produto.descricao);
    if (produto.foto) {
      setPreviewFoto(`${BASE_URL}${produto.foto}`);
    }
    setShowForm(true);
  };

  const handleDesativar = async (id) => {
    if (window.confirm('Deseja realmente desativar este produto?')) {
      try {
        await axios.delete(`${API_URL}/produtos/${id}`);
        alert('Produto desativado!');
        carregarProdutos();
      } catch (error) {
        alert('Erro ao desativar produto');
      }
    }
  };

  const cancelarEdicao = () => {
    setNome('');
    setPreco('');
    setDescricao('');
    setFoto(null);
    setPreviewFoto('');
    setEditando(null);
    setShowForm(false);
  };

  return (
    <div className="tela-produtos">
      <div className="header-section">
        <h2>ðŸ“¦ Produtos</h2>
        <button onClick={() => setShowForm(!showForm)} className="btn-novo">
          {showForm ? 'âŒ Cancelar' : 'âž• Novo Produto'}
        </button>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="form-produto">
          <h3>{editando ? 'âœï¸ Editar Produto' : 'âž• Novo Produto'}</h3>

          <div className="form-group">
            <label>Foto do Produto</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleFotoChange}
              style={{ padding: '0.5rem', fontSize: '0.9rem' }}
            />
            <small style={{ color: '#666', fontSize: '0.85rem' }}>
              Tamanho mÃ¡ximo: 5MB. Formatos aceitos: JPG, PNG, GIF
            </small>
            {previewFoto && (
              <div style={{ marginTop: '1rem' }}>
                <img 
                  src={previewFoto} 
                  alt="Preview" 
                  style={{ maxWidth: '300px', maxHeight: '200px', borderRadius: '8px' }}
                />
              </div>
            )}
          </div>

          <div className="form-group">
            <label>Nome do Produto *</label>
            <input
              type="text"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>PreÃ§o por Pessoa *</label>
            <input
              type="number"
              step="0.01"
              min="0"
              value={preco}
              onChange={(e) => setPreco(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>DescriÃ§Ã£o</label>
            <textarea
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
              rows="3"
            />
          </div>
          <div className="form-buttons">
            <button type="submit" className="btn-salvar">
              ðŸ’¾ {editando ? 'Atualizar' : 'Salvar'} Produto
            </button>
            {editando && (
              <button type="button" onClick={cancelarEdicao} className="btn-cancelar">
                Cancelar
              </button>
            )}
          </div>
        </form>
      )}

      <div className="lista-produtos">
        {produtos.map(produto => (
          <div key={produto.id} className="card-produto">
            {produto.foto && (
              <div style={{ marginBottom: '1rem' }}>
                <img 
                  src={`${BASE_URL}${produto.foto}`}
                  alt={produto.nome}
                  style={{ 
                    width: '100%', 
                    height: '180px', 
                    objectFit: 'cover', 
                    borderRadius: '8px' 
                  }}
                />
              </div>
            )}
            <h3>{produto.nome}</h3>
            <p className="preco">R$ {produto.preco.toFixed(2)} <span>por pessoa</span></p>
            {produto.descricao && <p className="descricao">{produto.descricao}</p>}
            <div className="card-actions">
              <button onClick={() => handleEditar(produto)} className="btn-editar">
                âœï¸ Editar
              </button>
              <button onClick={() => handleDesativar(produto.id)} className="btn-desativar">
                ðŸ—‘ï¸ Desativar
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

