            <label>Nome Completo *</label>
            <input
              type="text"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Username *</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Senha *</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              minLength="6"
            />
          </div>
          <div className="form-group">
            <label>Permiss√£o *</label>
            <select
              value={permissao}
              onChange={(e) => setPermissao(e.target.value)}
              required
            >
              <option value="usuario">üë®‚Äçüíº Usu√°rio (apenas vendas)</option>
              <option value="admin">üëë Admin (acesso total)</option>
            </select>
          </div>
          <button type="submit" className="btn-salvar">üíæ Salvar Operador</button>
        </form>
      )}

      <div className="lista-operadores">
        {operadores.map(op => (
          <div key={op.id} className="card-operador">
            <h3>{op.nome}</h3>
            <p>üë§ {op.username}</p>
            <span className={`badge ${op.permissao === 'admin' ? 'badge-admin' : 'badge-user'}`}>
              {op.permissao === 'admin' ? 'üëë Admin' : 'üë®‚Äçüíº Usu√°rio'}
            </span>
            <p className="data">Cadastrado em: {new Date(op.created_at).toLocaleDateString('pt-BR')}</p>

            {op.id !== userLogado.id && op.ativo === 1 && (
              <div className="card-actions" style={{ marginTop: '1rem' }}>
                <button 
                  onClick={() => handleDesativar(op.id, op.nome)} 
                  className="btn-desativar"
                  style={{ width: '100%' }}
                >
                  üóëÔ∏è Desativar Operador
                </button>
              </div>
            )}

            {op.id === userLogado.id && (
              <p style={{ color: '#27ae60', fontSize: '0.85rem', marginTop: '0.5rem', fontWeight: 600 }}>
                üë§ Voc√™ est√° logado com este usu√°rio
              </p>
            )}

            {op.ativo === 0 && (
              <p style={{ color: '#e74c3c', fontSize: '0.85rem', marginTop: '0.5rem', fontWeight: 600 }}>
                ‚ö†Ô∏è Operador desativado
              </p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

// ============= TELA DE RANKING =============
function TelaRanking() {
  const [periodo, setPeriodo] = useState('hoje');
  const [estatisticas, setEstatisticas] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    carregarEstatisticas();
  }, [periodo]);

  const carregarEstatisticas = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/vendas/estatisticas?periodo=${periodo}`);
      setEstatisticas(response.data);
    } catch (error) {
      alert('Erro ao carregar estat√≠sticas');
    } finally {
      setLoading(false);
    }
  };

  const getMedalha = (posicao) => {
    switch(posicao) {
      case 1: return 'ü•á';
      case 2: return 'ü•à';
      case 3: return 'ü•â';
      default: return `${posicao}¬∫`;
    }
  };

  const getTotalGeral = () => {
    return estatisticas.reduce((acc, item) => acc + item.valor_total, 0);
  };

  const getTotalVendas = () => {
    return estatisticas.reduce((acc, item) => acc + item.total_vendas, 0);
  };

  const periodos = [
    { value: 'hoje', label: 'üìÖ Hoje' },
    { value: 'ontem', label: 'üìÜ Ontem' },
    { value: 'ultimos7dias', label: 'üìä √öltimos 7 Dias' },
    { value: 'estemes', label: 'üìà Este M√™s' },
    { value: 'total', label: 'üèÜ Total Geral' }
  ];

  return (
    <div className="tela-ranking">
      <div className="header-section">
        <h2>üèÜ Ranking de Vendedores</h2>
      </div>

      <div className="filtro-periodo">
        {periodos.map(p => (
          <button
            key={p.value}
            className={`btn-periodo ${periodo === p.value ? 'active' : ''}`}
            onClick={() => setPeriodo(p.value)}
          >
            {p.label}
          </button>
        ))}
      </div>

      {loading ? (
        <div className="loading">Carregando estat√≠sticas...</div>
      ) : estatisticas.length === 0 ? (
        <div className="sem-dados">
          <p>üì≠ Nenhuma venda registrada neste per√≠odo</p>
        </div>
      ) : (
        <>
          <div className="resumo-geral">
            <div className="card-resumo">
              <div className="card-resumo-icon">üí∞</div>
              <div className="card-resumo-info">
                <span className="card-resumo-label">Valor Total</span>
                <span className="card-resumo-valor">R$ {getTotalGeral().toFixed(2)}</span>
              </div>
            </div>
            <div className="card-resumo">
              <div className="card-resumo-icon">üé´</div>
              <div className="card-resumo-info">
                <span className="card-resumo-label">Total de Vendas</span>
                <span className="card-resumo-valor">{getTotalVendas()}</span>
              </div>
            </div>
            <div className="card-resumo">
              <div className="card-resumo-icon">üë•</div>
              <div className="card-resumo-info">
                <span className="card-resumo-label">Vendedores</span>
                <span className="card-resumo-valor">{estatisticas.length}</span>
              </div>
            </div>
          </div>

          <div className="tabela-ranking">
            <table>
              <thead>
                <tr>
                  <th>Posi√ß√£o</th>
                  <th>Vendedor</th>
                  <th>Vendas</th>
                  <th>Valor Total</th>
                  <th>Ticket M√©dio</th>
                </tr>
              </thead>
              <tbody>
                {estatisticas.map((item) => (
                  <tr key={item.operador_id} className={`rank-${item.ranking}`}>
                    <td>
                      <span className="medalha">{getMedalha(item.ranking)}</span>
                    </td>
                    <td className="vendedor-nome">{item.operador_nome}</td>
                    <td className="texto-centro">{item.total_vendas}</td>
                    <td className="valor-destaque">R$ {item.valor_total.toFixed(2)}</td>
                    <td className="texto-centro">R$ {item.ticket_medio.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
