import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://srv1061141.hstgr.cloud:3001/api';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user') || 'null'));
  const [tela, setTela] = useState('venda');

  useEffect(() => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    }
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setToken(null);
    setUser(null);
  };

  if (!token) {
    return <TelaLogin setToken={setToken} setUser={setUser} />;
  }

  return (
    <div className="App">
      <header className="header">
        <h1>üé´ PDV Visite Campos</h1>
        <div className="user-info">
          <span>üë§ {user?.nome}</span>
          <button onClick={handleLogout} className="btn-logout">Sair</button>
        </div>
      </header>

      <nav className="nav-menu">
        <button 
          className={tela === 'venda' ? 'active' : ''} 
          onClick={() => setTela('venda')}
        >
          üõí Nova Venda
        </button>
        <button 
          className={tela === 'produtos' ? 'active' : ''} 
          onClick={() => setTela('produtos')}
        >
          üì¶ Produtos
        </button>
        <button 
          className={tela === 'historico' ? 'active' : ''} 
          onClick={() => setTela('historico')}
        >
          üìä Hist√≥rico
        </button>
        <button 
          className={tela === 'operadores' ? 'active' : ''} 
          onClick={() => setTela('operadores')}
        >
          üë• Operadores
        </button>
      </nav>

      <main className="main-content">
        {tela === 'venda' && <TelaVenda />}
        {tela === 'produtos' && <TelaProdutos />}
        {tela === 'historico' && <TelaHistorico />}
        {tela === 'operadores' && <TelaOperadores />}
      </main>
    </div>
  );
}

// ============= TELA DE LOGIN =============
function TelaLogin({ setToken, setUser }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [erro, setErro] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setErro('');
    setLoading(true);

    try {
      const response = await axios.post(`${API_URL}/login`, { username, password });
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.operador));
      setToken(response.data.token);
      setUser(response.data.operador);
    } catch (error) {
      setErro(error.response?.data?.error || 'Erro ao fazer login');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>üé´ Visite Campos</h1>
        <h2>Sistema PDV</h2>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Usu√°rio"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Senha"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {erro && <div className="erro">{erro}</div>}
          <button type="submit" disabled={loading}>
            {loading ? 'Entrando...' : 'Entrar'}
          </button>
        </form>
      </div>
    </div>
  );
}

// ============= TELA DE VENDA =============
function TelaVenda() {
  const [nomeCliente, setNomeCliente] = useState('');
  const [produtoId, setProdutoId] = useState('');
  const [quantidadePessoas, setQuantidadePessoas] = useState(1);
  const [desconto, setDesconto] = useState('');
  const [tipoDesconto, setTipoDesconto] = useState('real');
  const [produtos, setProdutos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [mensagem, setMensagem] = useState('');

  useEffect(() => {
    carregarProdutos();
  }, []);

  const carregarProdutos = async () => {
    try {
      const response = await axios.get(`${API_URL}/produtos`);
      setProdutos(response.data);
    } catch (error) {
      alert('Erro ao carregar produtos');
    }
  };

  const produtoSelecionado = produtos.find(p => p.id === parseInt(produtoId));
  const subtotal = produtoSelecionado ? produtoSelecionado.preco * quantidadePessoas : 0;
  
  let valorDesconto = 0;
  if (desconto && parseFloat(desconto) > 0) {
    if (tipoDesconto === 'percentual') {
      valorDesconto = (subtotal * parseFloat(desconto)) / 100;
    } else {
      valorDesconto = parseFloat(desconto);
    }
  }
  
  const total = subtotal - valorDesconto;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMensagem('');

    try {
      const response = await axios.post(`${API_URL}/vendas`, {
        nome_cliente: nomeCliente,
        produto_id: parseInt(produtoId),
        quantidade_pessoas: parseInt(quantidadePessoas),
        desconto: valorDesconto,
        tipo_desconto: desconto ? tipoDesconto : null
      });

      // Baixar PDF
      const pdfResponse = await axios.get(`${API_URL}/vendas/${response.data.id}/pdf`, {
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([pdfResponse.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `ingresso_${response.data.codigo_venda}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();

      setMensagem(`‚úÖ Venda #${response.data.codigo_venda} realizada com sucesso!`);
      
      // Limpar formul√°rio
      setNomeCliente('');
      setProdutoId('');
      setQuantidadePessoas(1);
      setDesconto('');
      
      setTimeout(() => setMensagem(''), 5000);
    } catch (error) {
      alert(error.response?.data?.error || 'Erro ao registrar venda');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="tela-venda">
      <h2>üõí Nova Venda</h2>
      
      {mensagem && <div className="mensagem-sucesso">{mensagem}</div>}

      <form onSubmit={handleSubmit} className="form-venda">
        <div className="form-group">
          <label>Nome do Cliente *</label>
          <input
            type="text"
            value={nomeCliente}
            onChange={(e) => setNomeCliente(e.target.value)}
            required
            placeholder="Digite o nome do cliente"
          />
        </div>

        <div className="form-group">
          <label>Produto *</label>
          <select
            value={produtoId}
            onChange={(e) => setProdutoId(e.target.value)}
            required
          >
            <option value="">Selecione um produto</option>
            {produtos.map(p => (
              <option key={p.id} value={p.id}>
                {p.nome} - R$ {p.preco.toFixed(2)} por pessoa
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Quantidade de Pessoas *</label>
          <input
            type="number"
            min="1"
            value={quantidadePessoas}
            onChange={(e) => setQuantidadePessoas(e.target.value)}
            required
          />
        </div>

        {produtoSelecionado && (
          <div className="resumo-valores">
            <div className="linha-valor">
              <span>Subtotal:</span>
              <span className="valor">R$ {subtotal.toFixed(2)}</span>
            </div>
          </div>
        )}

        <div className="desconto-section">
          <h3>Desconto (opcional)</h3>
          <div className="desconto-inputs">
            <div className="form-group">
              <label>Tipo de Desconto</label>
              <select
                value={tipoDesconto}
                onChange={(e) => setTipoDesconto(e.target.value)}
              >
                <option value="real">R$ (Reais)</option>
                <option value="percentual">% (Porcentagem)</option>
              </select>
            </div>
            <div className="form-group">
              <label>Valor do Desconto</label>
              <input
                type="number"
                step="0.01"
                min="0"
                value={desconto}
                onChange={(e) => setDesconto(e.target.value)}
                placeholder={tipoDesconto === 'real' ? '0.00' : '0'}
              />
            </div>
          </div>
          
          {desconto && parseFloat(desconto) > 0 && (
            <div className="resumo-valores">
              <div className="linha-valor desconto">
                <span>Desconto:</span>
                <span className="valor">- R$ {valorDesconto.toFixed(2)}</span>
              </div>
            </div>
          )}
        </div>

        {produtoSelecionado && (
          <div className="total-final">
            <span>TOTAL:</span>
            <span className="valor-total">R$ {total.toFixed(2)}</span>
          </div>
        )}

        <button type="submit" disabled={loading} className="btn-finalizar">
          {loading ? 'Processando...' : '‚úÖ Finalizar Venda'}
        </button>
      </form>
    </div>
  );
}

// ============= TELA DE PRODUTOS =============
function TelaProdutos() {
  const [produtos, setProdutos] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [nome, setNome] = useState('');
  const [preco, setPreco] = useState('');
  const [descricao, setDescricao] = useState('');

  useEffect(() => {
    carregarProdutos();
  }, []);

  const carregarProdutos = async () => {
    try {
      const response = await axios.get(`${API_URL}/produtos`);
      setProdutos(response.data);
    } catch (error) {
      alert('Erro ao carregar produtos');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_URL}/produtos`, { nome, preco, descricao });
      alert('Produto cadastrado com sucesso!');
      setNome('');
      setPreco('');
      setDescricao('');
      setShowForm(false);
      carregarProdutos();
    } catch (error) {
      alert('Erro ao cadastrar produto');
    }
  };

  return (
    <div className="tela-produtos">
      <div className="header-section">
        <h2>üì¶ Produtos</h2>
        <button onClick={() => setShowForm(!showForm)} className="btn-novo">
          {showForm ? '‚ùå Cancelar' : '‚ûï Novo Produto'}
        </button>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="form-produto">
          <div className="form-group">
            <label>Nome do Produto *</label>
            <input
              type="text"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Pre√ßo por Pessoa *</label>
            <input
              type="number"
              step="0.01"
              min="0"
              value={preco}
              onChange={(e) => setPreco(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Descri√ß√£o</label>
            <textarea
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
              rows="3"
            />
          </div>
          <button type="submit" className="btn-salvar">üíæ Salvar Produto</button>
        </form>
      )}

      <div className="lista-produtos">
        {produtos.map(produto => (
          <div key={produto.id} className="card-produto">
            <h3>{produto.nome}</h3>
            <p className="preco">R$ {produto.preco.toFixed(2)} <span>por pessoa</span></p>
            {produto.descricao && <p className="descricao">{produto.descricao}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}

// ============= TELA DE HIST√ìRICO =============
function TelaHistorico() {
  const [vendas, setVendas] = useState([]);

  useEffect(() => {
    carregarVendas();
  }, []);

  const carregarVendas = async () => {
    try {
      const response = await axios.get(`${API_URL}/vendas`);
      setVendas(response.data);
    } catch (error) {
      alert('Erro ao carregar hist√≥rico');
    }
  };

  const baixarPDF = async (id, codigo) => {
    try {
      const response = await axios.get(`${API_URL}/vendas/${id}/pdf`, {
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `ingresso_${codigo}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      alert('Erro ao baixar PDF');
    }
  };

  return (
    <div className="tela-historico">
      <h2>üìä Hist√≥rico de Vendas</h2>
      
      <div className="tabela-container">
        <table className="tabela-vendas">
          <thead>
            <tr>
              <th>C√≥digo</th>
              <th>Cliente</th>
              <th>Produto</th>
              <th>Pessoas</th>
              <th>Total</th>
              <th>Vendedor</th>
              <th>Data</th>
              <th>A√ß√µes</th>
            </tr>
          </thead>
          <tbody>
            {vendas.map(venda => (
              <tr key={venda.id}>
                <td>#{venda.codigo_venda}</td>
                <td>{venda.nome_cliente}</td>
                <td>{venda.produto_nome}</td>
                <td>{venda.quantidade_pessoas}</td>
                <td>R$ {venda.valor_total.toFixed(2)}</td>
                <td>{venda.operador_nome}</td>
                <td>{new Date(venda.created_at).toLocaleString('pt-BR')}</td>
                <td>
                  <button
                    onClick={() => baixarPDF(venda.id, venda.codigo_venda)}
                    className="btn-pdf"
                  >
                    üìÑ PDF
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

// ============= TELA DE OPERADORES =============
function TelaOperadores() {
  const [operadores, setOperadores] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [nome, setNome] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    carregarOperadores();
  }, []);

  const carregarOperadores = async () => {
    try {
      const response = await axios.get(`${API_URL}/operadores`);
      setOperadores(response.data);
    } catch (error) {
      alert('Erro ao carregar operadores');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_URL}/operadores`, { nome, username, password });
      alert('Operador cadastrado com sucesso!');
      setNome('');
      setUsername('');
      setPassword('');
      setShowForm(false);
      carregarOperadores();
    } catch (error) {
      alert(error.response?.data?.error || 'Erro ao cadastrar operador');
    }
  };

  return (
    <div className="tela-operadores">
      <div className="header-section">
        <h2>üë• Operadores</h2>
        <button onClick={() => setShowForm(!showForm)} className="btn-novo">
          {showForm ? '‚ùå Cancelar' : '‚ûï Novo Operador'}
        </button>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="form-operador">
          <div className="form-group">
            <label>Nome Completo *</label>
            <input
              type="text"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Username *</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Senha *</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              minLength="6"
            />
          </div>
          <button type="submit" className="btn-salvar">üíæ Salvar Operador</button>
        </form>
      )}

      <div className="lista-operadores">
        {operadores.map(op => (
          <div key={op.id} className="card-operador">
            <h3>{op.nome}</h3>
            <p>üë§ {op.username}</p>
            <p className="data">Cadastrado em: {new Date(op.created_at).toLocaleDateString('pt-BR')}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
